    // pull the player id from the URL
    const switcher = useParams();

    // const [ourPlayer, setOurPlayer] = useState({})

    let player = {};

    // console.log('switcher', singlePlayer)

    const handlePlayerfetch = async (id) => {
        const justOnePlayer = await getIndividualPlayer(id)
        console.log('this is our guy', justOnePlayer)
        player = justOnePlayer
        console.log('our guy in var', player)

    }

    console.log('our guy in var', player)
    handlePlayerfetch(singlePlayer)

    return (
        <p>You are on the {switcher.switcher} page.</p>
    )
}

export default PlayerCard
