import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { getIndividualPlayer } from "../api";

const PlayerCard = (props) => {

    const { singlePlayer } = props

    const [ourPlayer, setOurPlayer] = useState({})

    const switcher = useParams();

    // console.log('switcher', singlePlayer)

    const handlePlayerfetch = (id) => {
        getIndividualPlayer(id).then(result => setOurPlayer)
    }

    handlePlayerfetch(singlePlayer)

    return (
        <p>You are on the {switcher.switcher} page.</p>
    )
}

export default PlayerCard
